mapper.select:
SELECT

FROM industry_model_info im join model_classification_info mc on im.model_type=mc.classification_id
WHERE im.id=#{id}

SELECT

FROM industry_model_info im join model_classification_info mc on im.model_type=mc.classification_id
WHERE model_status=0 and del=0 

    and use_status=1


    and im.model_name like CONCAT('%',#{e.modelName},'%')


    and im.model_type = #{e.modelType}


    and im.Industry=#{e.industry}


    and im.is_toll=#{e.isToll}


    and im.use_status=#{e.useStatus}


    and im.user_id=#{e.userId}

SELECT
count(*)
FROM industry_model_info im 
WHERE model_status=0 and del=0

    and model_name like CONCAT('%',#{modelName},'%')


    and mode_type = #{modelType}


    and Industry=#{industry}


    and is_toll=#{isToll}


    and use_status=#{useStatus}
